import React from 'react';
import dateformat from 'dateformat';
import logo from './logo.svg';
import './App.css';
import todo from "./mockData.json"

function Header() {
    return (
        <header>
            <nav className="navbar navbar-light bg-light">
                <span className="navbar-brand">
                    Minimalist Todo List
                </span>
            </nav>
        </header>);
}

function List() {
    return (
        <ul className="list-group">
            {todo.Items.map(item => <Item key={item.id} item={item} />)}
        </ul>
    );
}

function Item({ item }) {
    const itemClass = `list-group-item list-group-item-${isOverdue(item)}`
    return (
        <li className={itemClass}>
            {`${item.name} - ${dateformat(new Date(item.timestampDeadline), "dd-mm-yyyy")}`}
        </li>
    );
}

function isOverdue(item) {
    return !item.complete && item.timestampDeadline < new Date().getTime();
}


export default Header;
