import React from 'react';
import dateformat from 'dateformat';
import './App.css';
import todo from "./mockData.json"
import Checkbox from './checkbox'


function Header() {
    const [items, setItems] = React.useState(todo.Items)
    const [filter, setFilter] = 
        React.useState({ overdueOnly: false, includeComplete: false });

    const updatedItems = todo.items.map(item => (item.id === id ? { ...item, complete: true } : item ));
    setItems(updatedItems)


    const filteredItems = items.filter(
        item => (filter.includeComplete || !item.complete) && (!filter.overdueOnly || isOverdue(item)),
    );
    return (
        <header>
            <nav className="navbar navbar-light bg-light">
                <span className="navbar-brand">
                    Minimalist Todo List
                </span>
            </nav>
        <Checkbox
            label="Overdue items only" selected={filter.overdueOnly}
            select={() => setFilter({...filter, overdueOnly: true})}
            unSelect={() => setFilter({...filter, overdueOnly: false})}
        />
        <Checkbox
            label="Include complete items" selected={filter.includeComplete}
            select={() => setFilter({...filter, includeComplete: true})}
            unSelect={() => setFilter({...filter, includeComplete: false})}
        />
        <div className="list">
            <List items={filteredItems} completeItem={complete} />
        </div>
        </header>
    );

}

function isOverdue(item) {
    return !item.complete && item.timestampDeadline < new Date().getTime();
}



function List({filter, completeItem}) {
    const filteredItems = todo.filter(item => 
        (filter.includeComplete || !item.complete) &&
        (!filter.overdueOnly || isOverdue(item)),
    );

    return (
        <ul className="list-group">
            {filteredItems.map(item => (
                <Item key={item.id} item={item} completeItem={() => completeItem(item.id)} />))}
        </ul>
    );
}


function Item({ item, completeItem  }) {
    const itemClass = `list-group-item list-group-item-${isOverdue(item)}`
    return (
        <li className={itemClass}>
            <div className="item">
                <span className={`item-title${item.complete ? " complete-item" : ""}`}>
                    {`${item.name} - ${dateformat(new Date(item.timestampDeadline), "dd-mm-yyyy")}`}
                </span>
                {!item.complete && (
                    <button type="button" className="btn btn-link" onClick={completeItem}>
                        Complete item
                    </button>)}
            </div>
        </li>);
}

export default Header;
