import React from "react";
import PropTypes from"prop-types";
import Item from "./item";
import isOverdue from "./isOverdue";

function List({filter, completeItem, isOverdue}) {
    const filteredItems = todo.Items.filter(item => 
        (filter.includeComplete || !item.complete) &&
        (!filter.overdueOnly || isOverdue(item)),
    );

    return (
        <ul className="list-group">
            {filteredItems.map(item => (
                <Item key={item.id} item={item} completeItem={() => completeItem(item.id)} />))}
        </ul>
    );
}

List.PropTypes = {
    items: PropTypes.array.isRequired,
    completeItem: PropTypes.func.isRequired,
}

export default List;
