import React from 'react';
import './App.css';
import todo from "./mockData.json";
import Checkbox from './checkbox';
import List from "./list";
import isOverdue from "./isOverdue";


function Header() {
    const [items, setItems] = React.useState(todo.Items)
    const [filter, setFilter] = 
        React.useState({ overdueOnly: false, includeComplete: false });
    const filteredItems = items.filter(
        item => (filter.includeComplete || !item.complete) && (!filter.overdueOnly || isOverdue(item)),
    );

    return (
        <header>
            <nav className="navbar navbar-light bg-light">
                <span className="navbar-brand">
                    Minimalist Todo List
                </span>
            </nav>
        <Checkbox
            label="Overdue items only" selected={filter.overdueOnly}
            select={() => setFilter({...filter, overdueOnly: true})}
            unSelect={() => setFilter({...filter, overdueOnly: false})}
        />
        <Checkbox
            label="Include complete items" selected={filter.includeComplete}
            select={() => setFilter({...filter, includeComplete: true})}
            unSelect={() => setFilter({...filter, includeComplete: false})}
        />
        <div className="list">
            <List items={filteredItems} />
        </div>
        </header>
    );

}

function completeItem({items, id}) {
    const updatedItems = items.map(item => (item.id === id ? { ...item, complete: true } : item ));
    return setItems(updatedItems)
}









export default Header;
